import React, { useContext, useState } from 'react';
import {Context} from '../context/Provider';

function FiltersList() {
  const [hiddenInput, setHiddenInput] = useState(true);
  const { setDaysFilter, daysFilter } = useContext(Context);

  return(
    <div className='filtersList'>
      <div className='companyFilter'>
        <button
          className='buttons'
          id='btn-company'
          onClick={() => setHiddenInput(!hiddenInput)}
        >
          Offer Jobs by Company Name
        </button>
        <input className='input' type='text' placeholder='Filter by company name' hidden={hiddenInput} />
      </div>
      <button className='buttons' id='btn-days' onClick={() => setDaysFilter(!daysFilter)}>
        Last 7 days
      </button>
      <button className='buttons' id='btn-clear'>Clear filters</button>
    </div>
  );
}

export default FiltersList;
