import React, { useContext, useEffect, useState } from 'react';
import apiGetJobs from '../api_call/getJobs';
import jobsList from '../api_call/manualLibrary';
import FiltersList from '../components/FiltersList';
import JobCard from '../components/JobCard';
import { Context } from '../context/Provider';
import '../styles/jobsPage.css';
import { getCompanyFiltered, getDaysFiltered, getTenFiltered } from '../utils/JobsFilters';

function Jobs() {
  const [jobs, setJobs] = useState([]);
  const { daysFilter, companyFilter } = useContext(Context);
  useEffect(() => {
    const getJobs = async () => {
      setJobs(await apiGetJobs() || jobsList);
    }
    getJobs();
    getTenFiltered(jobs);
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  // console.log(jobs);

  useEffect(() => {
    if(daysFilter) {
      setJobs(getDaysFiltered(jobs))
    };
    if(companyFilter) {
      setJobs(getCompanyFiltered(jobs));
    };
    // getTenFiltered(jobs);
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [daysFilter, companyFilter, jobs]);

  return(
    <div className='jobsPage'>
      <h1 className='pageTitle'>BUSINESS ANALYST JOBS NEAR ME</h1>
      <FiltersList />
      <div className='jobsList'>
        {
          jobs.length > 0 ? 
            jobs.map((job) => (
              <JobCard key={ job.id } job={ job }/>
            ))
          : 
            jobsList.map((job) => (
              <JobCard key={ job.id } job={ job }/>
            ))
        }
      </div>
    </div>
  );
}

export default Jobs;
