import React, { useEffect, useState } from 'react';
import apiGetJobs from '../api_call/getJobs';
import jobsList from '../api_call/manualLibrary';
import JobCard from '../components/JobCard';
import '../styles/jobsPage.css';

function Jobs() {
  const [jobs, setJobs] = useState([]);
  useEffect(() => {
    const getJobs = async () => {
      setJobs(await apiGetJobs());
    }
    getJobs();
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  console.log(jobs);

  return(
    <div className='jobsPage'>
      <h1 className='pageTitle'>BUSINESS ANALYST JOBS NEAR ME</h1>
      <div className='filtersList'>
        <div className='companyFilter'>
          <button className='buttons' id='btn-company'>Offer Jobs by Company Name</button>
          <input className='input' type='text' placeholder='Filter by company name' />
        </div>
        <button className='buttons' id='btn-days'>Last 7 days</button>
        <button className='buttons' id='btn-clear'>Clear filters</button>
      </div>
      <div className='jobsList'>
        {
          jobs.length > 0 ? 
            jobs.map((job) => (
              <JobCard key={ job.id } job={ job }/>
            ))
          : 
            jobsList.map((job) => (
              <JobCard key={ job.id } job={ job }/>
            ))
        }
      </div>
    </div>
  );
}

export default Jobs;
