import React, { useContext, useState } from 'react';
import {Context} from '../context/Provider';

function FiltersList() {
  const [hiddenInput, setHiddenInput] = useState(true);
  const [daysFilterActive, setDaysFilterActive] = useState(false);
  const { setDaysFilter, daysFilter, setCompanyFilter, companyFilter } = useContext(Context);

  return(
    <div className='filtersList'>
      <div className='companyFilter'>
        <button
          className='buttons'
          id='btn-company'
          onClick={() => setHiddenInput(!hiddenInput)}
        >
          Offer Jobs by Company Name
        </button>
        <input
          className='input'
          type='text'
          placeholder='Filter by company name'
          hidden={hiddenInput}
          onChange={(e) => setCompanyFilter(e.target.value)}
          value={companyFilter || ''}
        />
      </div>
      <button
        className='buttons'
        id='btn-days'
        onClick={(e) => {
          setDaysFilter(!daysFilter);
          e.target.className="";
        }}
      >
        Last 7 days
      </button>
      <button
        className='buttons'
        id='btn-clear'
        onClick={() => { setDaysFilter(false); setCompanyFilter('');}}
      >
        Clear filters
      </button>
    </div>
  );
}

export default FiltersList;
